mapper.delete:
DELETE
FROM Score
WHERE stuId = #{stuId}
  AND subId = #{subId}

mapper.update:
UPDATE Score
SET subScore  = #{subScore},
    subName   = #{subName},
    gmtCreate = #{gmtCreate},
    gmtModify = #{gmtModify}
WHERE stuId = #{stuId}
  AND subId = #{subId}

mapper.select:
SELECT *
FROM Score
WHERE stuId = #{stuId}
  AND subId = #{subId}

SELECT *
FROM Score
WHERE stuId = #{stuId}
   OR subId = #{subId}
   OR subName = #{subName}
   OR subScore = #{subScore}
