mapper.delete:
DELETE
FROM Score
WHERE stuId = #{stuId}
AND subId = #{subId}

mapper.update:
UPDATE Score
SET valid = 0,
    utcModify = #{utcModify},
    modifyBy = #{modifyBy}
WHERE stuId = #{stuId}
AND subId = #{subId}

UPDATE Score
SET valid = 1,
    utcModify = #{utcModify},
    modifyBy = #{modifyBy}
WHERE stuId = #{stuId}
  AND subId = #{subId}

mapper.select:
SELECT *
FROM Score
WHERE (scoId = #{scoId}
OR stuId = #{stuId}
OR subId = #{subId}
OR subScore = #{subScore}
OR remark = #{remark}
OR utcCreate = #{utcCreate}
OR utcModify = #{utcModify}
OR modifyBy = #{modifyBy}
OR valid = #{valid})

SELECT *
FROM Score
WHERE stuId = #{stuId}
AND subId = #{subId}
