mapper.update:
UPDATE Score
SET isReal = 0,
utcModify = #{utcModify}
WHERE stuId = #{stuId}
AND subId = #{subId}

UPDATE Score
SET isReal = 0,
utcModify = #{utcModify}
WHERE stuId = #{stuId}

UPDATE Score


        subScore = #{subScore},


        utcModify = #{utcModify},


WHERE stuId = #{stuId}
AND subId = #{subId}

mapper.select:
SELECT *
FROM Score
WHERE stuId = #{stuId}
AND subId = #{subId}
AND isReal = 1

SELECT *
FROM Score
WHERE (stuId = #{stuId}
OR subId = #{subId}
OR subScore = #{subScore}
OR utcCreate = #{utcCreate}
OR utcModify = #{utcModify})
AND isReal = 1
