mapper.update:
UPDATE Student
SET isReal    = 0,
    utcModify = #{utcModify}
WHERE stuId = #{stuId}

mapper.select:
SELECT *
FROM Student
WHERE (stuId = #{stuId}
    OR stuAge = #{stuAge}
    OR stuEmail = #{stuEmail}
    OR stuGender = #{stuGender}
    OR stuName = #{stuName}
    OR utcCreate = #{utcCreate}
    OR utcModify = #{utcModify})
  AND isReal = 1
