mapper.update:
UPDATE Student
SET isReal = 0,
gmtModify = #{gmtModify}
WHERE stuId = #{stuId}

mapper.select:
SELECT *
FROM Student
WHERE (stuId = #{stuId}
   OR stuAge = #{stuAge}
   OR stuEmail = #{stuEmail}
   OR stuGender = #{stuGender}
   OR stuName = #{stuName}
   OR gmtCreate = #{gmtCreate}
   OR gmtModify = #{gmtModify})
   AND isReal = 1
