mapper.update:
UPDATE Subject
SET isReal = 0,
utcModify = #{utcModify}
WHERE subId = #{subId}

mapper.select:
SELECT *
FROM Subject
WHERE (subId = #{subId}
OR subName = #{subName}
OR subTeacherId = #{subTeacherId}
OR subCredit = #{subCredit}
OR utcCreate = #{utcCreate}
OR utcModify = #{utcModify})
AND isReal = 1
