mapper.update:
UPDATE Subject
SET isReal = 0,
gmtModify = #{gmtModify}
WHERE subId = #{subId}

mapper.select:
SELECT *
FROM Subject
WHERE (subId = #{subId}
   OR subName = #{subName}
   OR subTeacherId = #{subTeacherId}
   OR subCredit = #{subCredit}
   OR gmtCreate = #{gmtCreate}
   OR gmtModify = #{gmtModify})
   AND isReal = 1
